<?php    include "../../php/db/db_manipulation.php";    if (isset($_GET['id'])) {            $id=$_GET['id'];            switch ($_GET['field']) {                case 'promote':                    change_role(1,$id);                    break;                case 'demote':                    change_role(0,$id);                    break;                case 'block':                    change_block(1, $id);                    break;                case 'unblock':                    change_block(0, $id);                    break;            }            }?>