<?phpuse PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\Exception;require "../../mail/Exception.php";require '../../mail/PHPMailer.php';require '../../mail/SMTP.php';function send($email, $token){    $mail = new PHPMailer;$mail->isSMTP(); $mail->SMTPDebug = 2; // 0 = off (for production use) - 1 = client messages - 2 = client and server messages$mail->Host = "smtp.gmail.com"; // use $mail->Host = gethostbyname('smtp.gmail.com'); // if your network does not support SMTP over IPv6$mail->Port = 587; // TLS only$mail->SMTPSecure = 'tls'; // ssl is deprecated$mail->SMTPAuth = true;$mail->Username = 'web2020fmi@gmail.com'; // email$mail->Password = 'hardparola'; // password$mail->setFrom('web2020fmi@gmail.com', 'Test Testov'); // From email and name$mail->addAddress($email, $email); // to email and name$mail->Subject = 'Password recovery code';$mail->msgHTML("Recovery code: $token <br> Copy it and place it in the Puffin website->Forgotten password->I have a recovery code"); //$mail->msgHTML(file_get_contents('contents.html'), __DIR__); //Read an HTML message body from an external file, convert referenced images to embedded,$mail->AltBody = 'HTML messaging not supported'; // If html emails is not supported by the receiver, show this body// $mail->addAttachment('images/phpmailer_mini.png'); //Attach an image file$mail->SMTPOptions = array(                    'ssl' => array(                        'verify_peer' => false,                        'verify_peer_name' => false,                        'allow_self_signed' => true                    )                );ob_start();if(!$mail->send()){    echo "Mailer Error: " . $mail->ErrorInfo;}else{    echo "Message sent!";}ob_end_clean();}