<?php     session_start();        include_once "../../php/db/db_manipulation.php";    $comment = $_POST["comment"];    $user = $_SESSION['uname'];    $fn = getID($user);    $theme = getTheme($user, $fn);    $version = getVersion($fn, 'inv');	if($version != 0){		$version = $version['version'];	}	else $version = 0;    $theme_id = getThemeID($fn); 	$target_dir = "./inv/";	$target_file = $target_dir . $fn . "_invite_" . $version;	$imageFileType = strtolower(pathinfo(basename($_FILES["image"]["name"]),PATHINFO_EXTENSION));	$target_file .= "." . $imageFileType;	if($imageFileType != "jpg" && $imageFileType != "png" && $imageFileType != "jpeg" && $imageFileType != "gif" ) {		echo "Разрешените формати на изображението са JPG, JPEG, GIF или PNG.";	}	else{	    $check = getimagesize($_FILES["image"]["tmp_name"]);		if($check == false) {		echo "Файлът не е Изображение.";		}	elseif(strlen(basename($_FILES["image"]["name"])) == 0){		echo "Не е избрано изображение!";		}  	elseif ($_FILES["image"]["size"] = 0) {	  echo "Не е избрано Изображение.";	}	elseif ($_FILES["image"]["size"] > 500000) {	 echo "Файлът е прекалено голям. Максималният размер е 10MB.";	}	elseif($imageFileType != "jpg" && $imageFileType != "png" && $imageFileType != "jpeg" && $imageFileType != "gif" ) {		echo "Разрешените формати на изображението са JPG, JPEG, GIF или PNG.";	}		  	else{  	move_uploaded_file($_FILES["image"]["tmp_name"], $target_file);      addFile('inv', $user, $fn, $theme_id, $version, $comment, $target_file);  	}	}	  ?>